import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import web3 from './web3'
import lottery from './lottery'

class App extends Component  {
  //creation and initiatonlization state
  constructor(props){
    super(props)
    //manager state set with default empty string
    this.state = {manager : '' }
  }

  /*
  state = {
    manager : ''  EQUIVALENT TO ABOVE C
  }
  */

  async componentDidMount(){
    //getting manager from contract
    const manager = await lottery.methods.manager().call()
    //setting state manager with manager, After setting the state render() method is called automatically
    this.setState({manager : manager})
  }

render() {
  return (
    <div>
      <h2>Lottery Contract</h2>
  <p>This Contract Is Managed By {this.state.manager}{/*calling the manager property inside state*/}</p>
    </div>
  );
}
}

export default App;
